    private fun setMqttCallBack() {
        mq.setCallback(object : MqttCallbackExtended {
            override fun connectComplete(b: Boolean, s: String) {
                val snackbarMsg = "Connected to host:\n'$SOLACE_MQTT_HOST'."
                Log.w( TAG, snackbarMsg)
            }

            override fun connectionLost(throwable: Throwable) {
                val snackbarMsg = "Connection to host lost:\n'$SOLACE_MQTT_HOST'"
                Log.w( TAG, snackbarMsg)
            }
            @Throws(Exception::class)
            override fun messageArrived(topic: String, mqttMessage: MqttMessage) {
                Log.w(TAG, "Message received from host '$SOLACE_MQTT_HOST': $mqttMessage")
            }

            override fun deliveryComplete(iMqttDeliveryToken: IMqttDeliveryToken) {
                Log.w("Debug", "Message published to host '$SOLACE_MQTT_HOST'")
            }
        })
    }

    override fun onItemSelected(parent: AdapterView<*>, view: View?, pos: Int, id: Long) {
        // An item was selected. You can retrieve the selected item using
        // parent.getItemAtPosition(pos)
        sensorSelected = parent.getItemAtPosition(pos).toString()
        var selectedSensorMode : Array<String> = this.sensorList.getSensorMode(sensorSelected)

        val v: View? = getView()
        //val activity = FragmentActivity()
        //val sensorModeSpinner = v?.findViewById<Spinner>(R.id.sensorMode)
        //val adapter = ArrayAdapter<String>(requireContext(), android.R.layout.simple_spinner_dropdown_item, selectedSensorMode)
        //adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item)
        if (v != null){
            val parentLinearLayout: LinearLayout = v.findViewById(R.id.linearLayout)
            parentLinearLayout.removeAllViews()
            for (item in selectedSensorMode){
                val buttonObj: Button = Button(requireContext())
                buttonObj.text = item
                buttonObj.setTag(R.string.name,sensorSelected)
                parentLinearLayout.addView(buttonObj)
                buttonObj.setOnClickListener(this)
            }

        }

//        if (sensorModeSpinner != null) {
//            sensorModeSpinner.adapter = adapter
//        }

        //    findViewById(R.id.sensorMode)
        Log.e(TAG, view.toString())
    }


    //https://maker.ifttt.com/trigger/{event}/with/key/bUSli4cRd920cAJadVLV1h
    @UiThread
    fun makeNetworkRequest(eventName: String){
        {
            val result = URL("https://maker.ifttt.com/trigger/$eventName/with/key/bUSli4cRd920cAJadVLV1h").readText()
            Log.e(TAG,result.toString())

        }

    }

    override fun onClick(v: View?){
        val b: Button = v as Button
        Log.e(TAG, b.text.toString())
        val tag:String = b.getTag(R.string.name) as String
        if (tag == "desk"){
            mq.publish("LED/desk/mode",b.text.toString())
        } else {
            var eventName: String
            if (b.text.toString() == "ON"){
                eventName = "${tag}_turn_on"
            } else {
                eventName = "${tag}_turn_off"
            }
            Log.e(TAG, eventName)
            try {
                IFTTT.iftttService.triggerService("den_turn_off").enqueue(object: Callback<String>{
                    override fun onResponse(call: Call<String>, response: Response<String>) {
                        Log.e(TAG, response.code().toString())
                    }

                    override fun onFailure(call: Call<String>, t: Throwable) {
                        Log.e(TAG, t.message.toString())
                    }
                })
                //val coroutineScope = CoroutineScope(Dispatchers.Main + parentJob)
                //viewModelScope.launch {
                    //val result = makeNetworkRequest(eventName)
                //    Log.e(TAG,result.toString())
                //}

            } catch (e: java.lang.Exception){
                Log.e(TAG, e.toString())
            }


        }

    }

